import React, { Component } from "react";

import { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { editPaint, getPaint } from "../service/api";

// Material UI
import {
  FormGroup,
  FormControl,
  InputLabel,
  Typography,
  styled,
  TextField,
  Button,
} from "@mui/material";

// MultiSelect
import { useTheme } from "@mui/material/styles";
import MenuItem from "@mui/material/MenuItem";
import Select from "@mui/material/Select";

const Container = styled(FormGroup)`
  margin: 3% auto auto auto;
  width: 50%;
  & > div {
    margin-top: 20px;
    margin-bottom: 20px;
  }
`;

let defaultPaint = {
  color: "",
  status: "",
  quantity: "",
};

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250,
    },
  },
};

// Temp
const names = ["Out of Stock", "Available", "Running Low"];

// export default class EditPaint extends Component {
//   render() {
//     return <div>Heylo!</div>;
//   }
// }

const EditPaint = () => {
  const [open, setOpen] = useState(false);
  const [paint, setPaint] = useState(defaultPaint);
  const [errors, setErrors] = useState({});

  const navigate = useNavigate();

  const { id } = useParams();

  useEffect(() => {
    const loadPaintDetails = async () => {
      const response = await getPaint(id);
      const paint = response.data;
      setPaint(paint);
      setStatus(paint.status);
    };
    loadPaintDetails();
  }, []);

  const [status, setStatus] = useState([]);
  const theme = useTheme();

  const onValueChange = (e) => {
    setPaint({ ...paint, [e.target.name]: e.target.value });
  };

  const handleChange = (event) => {
    setStatus(event.target.value);
  };

  const handleClick = (paint) => {
    defaultPaint = paint;
    setOpen(true);
  };

  const editPaintDetails = async () => {
    await editPaint(paint, id);
    navigate("/");
  };

  return (
    <Container>
      <Typography variant="h4">Update Paint Inventory</Typography>
      <br />
      <FormControl>
        <TextField
          label="Color"
          variant="standard"
          name="color"
          value={paint.color}
          onChange={(e) => onValueChange(e)}
          disabled
        />
      </FormControl>
      <FormControl>
        <TextField
          id="outlined-number"
          label="Quantity in Litres"
          type="number"
          onChange={(e) => onValueChange(e)}
          name="quantity"
          value={paint.quantity}
        />
      </FormControl>

      <FormControl>
        <InputLabel id="demo-single-select-label">Status</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={status}
          label="Status"
          onChange={handleChange}
        >
          <MenuItem value="Running Low">Running Low</MenuItem>
          <MenuItem value="Available">Available</MenuItem>
          <MenuItem value="Out of Stock">Out of Stock</MenuItem>
        </Select>
      </FormControl>

      <FormControl>
        <Button variant="contained" onClick={(e) => handleClick(paint)}>
          Save Changes
        </Button>
        <Dialog open={open} onClose={() => setOpen(false)}>
          <DialogTitle>Save The Changes</DialogTitle>
          <DialogContent>
            <DialogContentText>
              Please confirm if you want to save the changes?
            </DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpen(false)} color="primary">
              Cancel
            </Button>
            <Button onClick={handleConfirm} color="secondary" autoFocus>
              Confirm
            </Button>
          </DialogActions>
        </Dialog>
      </FormControl>
    </Container>
  );
};

export default EditPaint;
