import React from "react";
import { useEffect, useState } from "react";

import {
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  styled,
  Button,
  Dialog,
  DialogTitle,
  DialogContentText,
  DialogContent,
  DialogActions,
  TextField,
  Typography,
  Grid,
} from "@mui/material";
import { Card, CardContent } from "@mui/material";

import { Link } from "react-router-dom";
import { getPaints } from "../service/api";

const GridContainer = styled(Grid)({
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
});

const Container = styled(TextField)`
  width: 20%;
  margin: 10px 20px 0 20px;
`;

const Total = styled(Typography)`
  justifycontent: center;
  margin: 30px 0 0 20px;
`;

const StyledTable = styled(Table)`
  width: 90%;
  margin: 30px auto 0 auto;
`;
const THead = styled(TableRow)`
  position: sticky;
  top: 0;
  z-index: 1;
  background-color: black;
  & > th {
    color: white;
    font-size: 15px;
  }
`;
const TBody = styled(TableRow)`
  & > td {
    font-size: 14px;
  }
`;

const PaintsList = () => {
  const [paints, setPaints] = useState([]);

  useEffect(() => {
    getAllPaints();
  }, []);

  const getAllPaints = async () => {
    let response = await getPaints();
    setPaints(response.data);
  };

  return (
    <div>
      {/* <StyledTable>
        <TableHead>
          <THead>
            <TableCell>Color</TableCell>
            <TableCell>Status</TableCell>
            <TableCell>Quantity</TableCell>
            <TableCell>Created By</TableCell>
            <TableCell>Last Modified By</TableCell>
            <TableCell></TableCell>
          </THead>
        </TableHead>
        <TableBody>
          {paints.map((paint) => (
            <TBody key={paint._id}>
              <TableCell> {paint.color} </TableCell>
              <TableCell> {paint.status} </TableCell>
              <TableCell> {paint.quantity} </TableCell>
              <TableCell> {paint.createdBy} </TableCell>
              <TableCell> {paint.lastModifiedBy} </TableCell>
             
            </TBody>
          ))}
        </TableBody>
      </StyledTable> */}
      {paints.map((paint) => (
        <Card key={paint._id} variant="outlined">
          <CardContent>
            <Typography variant="h5" component="div">
              Color: {paint.color}
            </Typography>
            <Typography variant="body1">Status: {paint.status}</Typography>
            <Typography variant="body1">Quantity: {paint.quantity}</Typography>
            <Typography variant="body1">
              Created By: {paint.createdBy}
            </Typography>
            <Typography variant="body1">
              Last Modified By: {paint.lastModifiedBy}
            </Typography>
          </CardContent>
        </Card>
      ))}
    </div>
  );
};

export default PaintsList;
