import React from "react";
import { useEffect, useState } from "react";

import { Card, CardContent, Typography, Grid } from "@mui/material";

import { Link } from "react-router-dom";
import { getPaints } from "../service/api";

const PaintsList = () => {
  const [paints, setPaints] = useState([]);

  const swimLanes = {
    "Running Low": [],
    Available: [],
    "Out of Stock": [],
  };

  paints.forEach((paint) => {
    if (paint.status === "Running Low") {
      swimLanes["Running Low"].push(paint);
    } else if (paint.status === "Available") {
      swimLanes.Available.push(paint);
    } else if (paint.status === "Out of Stock") {
      swimLanes["Out of Stock"].push(paint);
    }
  });

  useEffect(() => {
    getAllPaints();
  }, []);

  const getAllPaints = async () => {
    let response = await getPaints();
    setPaints(response.data);
  };

  return (
    <div style={{ width: "100%" }}>
      <Grid container spacing={2} sx={{ margin: "20px", width: "80%" }}>
        {Object.entries(swimLanes).map(([status, paints]) => (
          <Grid
            item
            key={status}
            xs={12}
            sm={6}
            md={paints.length > 2 ? 6 : 4}
            lg={paints.length > 2 ? 6 : 3}
          >
            <Card variant="outlined">
              <CardContent>
                <Typography variant="h5" component="div">
                  {status}
                </Typography>
              </CardContent>
              <CardContent>
                {paints.map((paint) => (
                  <Card
                    key={paint._id}
                    variant="outlined"
                    sx={{ marginBottom: "10px" }}
                  >
                    <CardContent>
                      <Typography variant="h6" component="div">
                        Color: {paint.color}
                      </Typography>
                      <Typography variant="body1">
                        Quantity: {paint.quantity}
                      </Typography>
                      <Typography variant="body1">
                        Created By: {paint.createdBy}
                      </Typography>
                      <Typography variant="body1">
                        Last Modified By: {paint.lastModifiedBy}
                      </Typography>
                    </CardContent>
                  </Card>
                ))}
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </div>
  );
};

export default PaintsList;
