import React from "react";
import { useEffect, useState } from "react";

import {
  Card,
  CardContent,
  Typography,
  Grid,
} from "@mui/material";

import { Link } from "react-router-dom";
import { getPaints } from "../service/api";


const PaintsList = () => {
  const [paints, setPaints] = useState([]);

  useEffect(() => {
    getAllPaints();
  }, []);

  const getAllPaints = async () => {
    let response = await getPaints();
    setPaints(response.data);
  };

  return (
    <div>      
      <Grid container spacing={2} sx={{ margin: "20px" }}>
        {paints.map((paint) => (
          <Grid item key={paint._id} xs={12} sm={6} md={4} lg={3}>
            <Card variant="outlined">
              <CardContent>
                <Typography variant="h5" component="div">
                  Color: {paint.color}
                </Typography>
                <Typography variant="body1">Status: {paint.status}</Typography>
                <Typography variant="body1">
                  Quantity: {paint.quantity}
                </Typography>
                <Typography variant="body1">
                  Created By: {paint.createdBy}
                </Typography>
                <Typography variant="body1">
                  Last Modified By: {paint.lastModifiedBy}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </div>
  );
};

export default PaintsList;
